import { Component } from '@angular/core';

@Component({
  templateUrl: './article-create.component.html',
  selector: 'app-article-create',
})
export class ArticleCreateComponent {
  newArticle = 'NO CONTENT';

  onAddArticle(postInput: HTMLTextAreaElement) {
    console.log(postInput);
    this.newArticle = postInput.value;
  }
}
<textarea rows="6" [value]="newArticle" #postInput></textarea>
<hr />
<button (click)="onAddArticle(postInput)">Save Article</button>
<p>{{ newArticle }}</p>
