import { Component } from '@angular/core';

@Component({
  selector: 'app-article-list',
  templateUrl: './article-list.component.html',
  styleUrls: ['./article-list.component.css'],
})
export class ArticleListComponent {
  articles = [
    {
      title: 'First article',
      subtitle: 'Nice first article',
      content: 'blablablabla first article blabla',
      image: 'image.png',
      author: 'Dinis Costa',
    },
    {
      title: 'Second article',
      subtitle: 'Nice second article',
      content: 'blablablabla second article blabla',
      image: 'image.png',
      author: 'Dinis Costa',
    },
    {
      title: 'Third article',
      subtitle: 'Nice third article',
      content: 'blablablabla third article blabla',
      image: 'image.png',
      author: 'Dinis Costa',
    },
  ];
}
<mat-accordion>
  <mat-expansion-panel *ngFor="let article of articles">
    <mat-expansion-panel-header>{{ article.title }}</mat-expansion-panel-header>
    <p>{{ article.content }}</p>
  </mat-expansion-panel>
</mat-accordion>
