@Component({
  templateUrl: './article-create.component.html',
  selector: 'app-article-create',
})
export class ArticleCreateComponent {
  onAddArticle() {
    alert('Article added!');
  }
}
<textarea rows="6"></textarea>
<hr />
<button (click)="(onAddArticle)">Save Article</button>
