import { Component, EventEmitter, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Article } from '../article.model';

@Component({
  templateUrl: './article-create.component.html',
  selector: 'app-article-create',
  styleUrls: ['./article-create.component.css'],
})
export class ArticleCreateComponent {
  newArticle = {
    title: '',
    subtitle: '',
    content: '',
    image: '',
    author: '',
    createdAt: new Date(),
    updatedAt: new Date(),
  };
  content = '';
  title = '';
  subtitle = '';
  image = '';
  author = '';
  createdAt = new Date();
  updatedAt = new Date();
  @Output() articleCreated = new EventEmitter();

  onAddArticle(form: NgForm) {
    if (form.invalid) {
      return;
    }

    this.newArticle = {
      title: form.value.title,
      subtitle: form.value.subtitle,
      content: form.value.content,
      image: form.value.image,
      author: form.value.author,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    const article: Article = this.newArticle;

    this.articleCreated.emit(article);
  }
}
<mat-card>
  <form (submit)="onAddArticle(articleForm)" #articleForm="ngForm">
    <mat-form-field>
      <mat-label>Title</mat-label>
      <input
        matInput
        type="text"
        name="title"
        ngModel
        #title="ngModel"
        required
        minlength="10"
      />
      <mat-error *ngIf="title.invalid"
        >Please, enter an article title</mat-error
      >
    </mat-form-field>
    <mat-form-field>
      <mat-label>Subtitle</mat-label>
      <input
        matInput
        type="text"
        name="subtitle"
        ngModel
        required
        minlength="20"
      />
    </mat-form-field>
    <mat-form-field>
      <mat-label>Image</mat-label>
      <input matInput type="text" name="image" ngModel required />
    </mat-form-field>
    <mat-form-field>
      <mat-label>Content</mat-label>
      <textarea matInput rows="6" name="content" ngModel required></textarea>
    </mat-form-field>
    <button mat-raised-button color="primary" type="submit">
      Save Article
    </button>
  </form>
</mat-card>
