import { Component, Input, OnDestroy, OnInit } from '@angular/core';
import { Subscription } from 'rxjs';
import { Article } from '../article.model';
import { ArticlesService } from '../articles.service';

@Component({
  selector: 'app-article-list',
  templateUrl: './article-list.component.html',
  styleUrls: ['./article-list.component.css'],
})
export class ArticleListComponent implements OnInit, OnDestroy {
  @Input() articles: Article[] = [];

  private articlesSub: Subscription;

  constructor(public articlesService: ArticlesService) {}

  ngOnInit(): void {
    this.articles = this.articlesService.getArticles();

    this.articlesSub = this.articlesService
      .getArticleUpdateListener()
      .subscribe((articles: Article[]) => {
        this.articles = articles;
      });
  }

  ngOnDestroy(): void {
    this.articlesSub.unsubscribe();
  }
}
<mat-accordion multi="true" *ngIf="articles.length > 0">
  <mat-expansion-panel *ngFor="let article of articles">
    <mat-expansion-panel-header>{{ article.title }}</mat-expansion-panel-header>
    <p>{{ article.content }}</p>
    <mat-action-row>
      <button mat-button color="accent">EDIT</button>
      <button mat-button color="warn">DELETE</button>
    </mat-action-row>
  </mat-expansion-panel>
</mat-accordion>
<p class="info-text mat-body-1" *ngIf="articles.length <= 0">
  No articles added yet!
</p>
