import { Component, EventEmitter, Output } from '@angular/core';
import { Article } from '../article.model';

@Component({
  templateUrl: './article-create.component.html',
  selector: 'app-article-create',
  styleUrls: ['./article-create.component.css'],
})
export class ArticleCreateComponent {
  newArticle = {
    title: '',
    subtitle: '',
    content: '',
    image: '',
    author: '',
    createdAt: Date(),
    updatedAt: Date(),
  };
  enteredContent = '';
  enteredTitle = '';
  enteredSubtitle = '';
  enteredImage = '';
  enteredAuthor = '';
  enteredCreatedAt = new Date().toLocaleString();
  enteredUpdatedAt = new Date().toLocaleString();
  @Output() articleCreated = new EventEmitter();

  onAddArticle() {
    this.newArticle = {
      title: this.enteredTitle,
      subtitle: this.enteredSubtitle,
      content: this.enteredContent,
      image: this.enteredImage,
      author: this.enteredAuthor,
      createdAt: this.enteredCreatedAt,
      updatedAt: this.enteredUpdatedAt,
    };

    const article: Article = this.newArticle;

    this.articleCreated.emit(article);
  }
}
