import { Component, EventEmitter } from '@angular/core';

@Component({
  templateUrl: './article-create.component.html',
  selector: 'app-article-create',
  styleUrls: ['./article-create.component.css'],
})
export class ArticleCreateComponent {
  newArticle = { title: '', content: '' };
  enteredContent = '';
  enteredTitle = '';
  articleCreated = new EventEmitter();

  onAddArticle() {
    this.newArticle = {
      title: this.enteredTitle,
      content: this.enteredContent,
    };

    const article = this.newArticle;

    this.articleCreated.emit(article);
  }
}
<mat-card>
  <mat-form-field>
    <mat-label>Title</mat-label>
    <input matInput type="text" [(ngModel)]="enteredTitle" />
  </mat-form-field>
  <mat-form-field>
    <mat-label>Leave a comment</mat-label>
    <textarea matInput rows="6" [(ngModel)]="enteredContent"></textarea>
  </mat-form-field>
  <button mat-raised-button color="primary" (click)="onAddArticle()">
    Save Article
  </button>
</mat-card>
